import React from 'react'

export const UserTable = (props) => {

  const EditRow =(user) =>{
      console.log('hola')
  }
  const ListUsers = ()=>{
       return( 
           props.users.map( user =>(
            <tr key={ user.id }>
                <td>{ user.name }</td>
                <td>{ user.username }</td>
                <td>
                    <button
                        onClick={EditRow(user)}
                    >
                        Edit
                    </button>
                </td>
            </tr>

           ))
        )
  }
  const NotUsers = () =>{     
    return(
        <tr>
            <td colSpan={3}>No users</td>
        </tr>
    )
  }
  return (
    <>
        <table>
            <thead>
                <tr>
                    <th>Name</th>
                    <th>UserName</th>
                    <th>Actions</th>
                </tr>
            </thead>
            <tbody>
                {props.users.length > 0 ? ListUsers() : NotUsers() }
            </tbody>
        </table>        
    </>
  )
}
